export const theme = Object.freeze({
  colors: {
    white: '#ffffff',
    active: '#430766',
    grey: '#7b7580',
    greyBg: '#dc9f1d',
    greyOpacity: 'rgba(47, 48, 58, 0.1)',
    primaryText: '#1f0d4d',
    secondaryText: '#000000',
    buttonBg: '#b3f5d5',
    boxShadow:
      '0px 1px 3px rgba(0, 0, 0, 0.22), 0px 2px 2px rgba(0, 0, 0, 0.24), 0px 2px 1px rgba(0, 0, 0, 0.32)',
  },
  fonts: {
    secondary: "'Work Sans', sans-serif",
  },
});
